todoApp.controller('TodoCommentController', ['$scope', '$http', 'todo_listFactory', 'toastr', 'Upload',
  function($scope, $http, todo_listFactory, toastr, Upload) {
    $scope.submitTodoComment = function(todo_task) {
      todo_listFactory.setTodoTaskId(todo_task.id);
      todo_listFactory.submitTodoComment($scope.todo_commentData).success(function(data) {
        console.log(data);
        if($scope.files) {
          for (var i = 0; i < $scope.files.length; i++) {
            var file = $scope.files[i];
            Upload.upload({
              url: '/api/v1/todo_comments/' + data.id + '/todo_attachments',
              method: 'POST',
              file: file
            }).success(function(data) {
              $scope.files = [];
              todo_listFactory.getTodoTaskComments().success(function(data) {
                $scope.todo_task.todo_comments = data;
              });
            });
          }
        };
        $scope.todo_commentData = {};
        todo_listFactory.getTodoTaskTodoComments().success(function(data) {
          toastr.success('Comment successfully created.');
          $scope.todo_task.todo_comments = data;
        });
      })
      .catch(function(response) {
        toastr.error('Comment can\'t be blank.');
      });
    };

    $scope.deleteTodoComment = function(todo_taskId, todo_commentData) {
      var confirmation = confirm('Are you sure?');
      if (confirmation) {
        todo_listFactory.deleteTodoComment(todo_commentData).success(function() {
          todo_listFactory.getTodoTaskTodoComments(todo_taskId).success(function(data) {
            $scope.todo_task.todo_comments = data;
          });
        });
      };
    };

    $scope.deleteAttach = function(index) {
      if (index > -1) {
        $scope.files.splice(index, 1);
      }
    };
}]);
todoApp.controller('TodoListController', ['$scope', '$http', 'todo_listFactory', 'toastr',
  function($scope, $http, todo_listFactory, toastr) {
    $scope.todo_listData = {};

    $scope.getTodoLists = function() {
      todo_listFactory.getTodoLists().success(function(data) {
        $scope.todo_lists = data;
      });
    };

    $scope.submitTodoList = function() {
      todo_listFactory.submitTodoList($scope.todo_listData).success(function() {
        $scope.todo_listData = {};
        $scope.getTodoLists();
      });
    };

    $scope.deleteTodoList = function(id) {
      var confirmation = confirm('Are you sure?');
      if(confirmation) {
        todo_listFactory.deleteTodoList(id).success(function() {
          $scope.getTodoLists();
        });
      }
    };

    $scope.TodoListEdit = function() {
      $(event.target).closest('div').toggleClass('editing');
    };

    $scope.editTodoListOnEnter = function(todo_list) {
      if(!todo_list.title) {
        toastr.error('Project name can\'t be blank.');
      };
      if(event.keyCode == 13 && todo_list.title) {
        todo_listFactory.updateTodoList(todo_list);
        $scope.TodoListEdit();
      }
    };

    $scope.toggleTodoListEdit = function() {
      $(event.target).parents().eq(1).toggleClass('editing');
    };

    $scope.sortableOptions = {
      stop: function(e, ui) {
        _.map($scope.todo_lists, function(todo_list, index) {
          todo_listFactory.updateTodoList({id: todo_list.id, priority: index})
        })
      },
      axis: 'y'
    };

    $scope.getTodoLists();
}]);
todoApp.controller('TodoTaskController', ['$scope', '$http', 'todo_listFactory', 'toastr',
  function($scope, $http, todo_listFactory, toastr) {
    $scope.submitTodoTask = function(todo_listId) {
      todo_listFactory.setTodoListId(todo_listId);
      todo_listFactory.submitTodoTask($scope.todo_taskData).success(function() {
        $scope.todo_taskData = {};
        todo_listFactory.getTodoListTodoTasks(todo_listId).success(function(data) {
          $scope.todo_list.todo_tasks = data;
        });
      })
      .catch(function(response) {
        toastr.error('Task can\'t be blank.');
      });
    };

    $scope.updateTodoTask = function(todo_taskData) {
      todo_listFactory.updateTodoTask(todo_taskData);
    };

    $scope.deleteTodoTask = function(todo_listId, todo_taskData) {
      var confirmation = confirm('Are you sure?');
      if (confirmation) {
        todo_listFactory.deleteTodoTask(todo_taskData).success(function() {
          todo_listFactory.getTodoListTodoTasks(todo_listId).success(function(data) {
            $scope.todo_list.todo_tasks = data;
          });
        });
      };
    };

    $scope.todo_taskEdit = function() {
      $(event.target).closest('div').toggleClass('editing');
    };

    $scope.editTodoTaskOnEnter = function(todo_task) {
      if(!todo_task.content) {
        toastr.error('Task content can\'t be blank.');
      };
      if(event.keyCode == 13 && todo_task.content) {
        $scope.updateTodoTask(todo_task);
        $scope.todo_taskEdit();
      };
    };

    $scope.toggleTodoTaskEdit = function() {
      $(event.target).closest('tr').find('td.todo_task-content div').
        toggleClass('editing');
    };

    $scope.toggleTodoComments = function() {
      $(event.target).closest('tr').find('td.todo_task-content div.todo_comments').
        toggleClass('todo_comments-on');
    };

    $scope.setDue = function(todo_task) {
      $scope.updateTodoTask(todo_task);
      $scope.optionsTodoTask = false;
    };

    $scope.sortableOptions = {
      stop: function(e, ui) {
        _.map($scope.todo_list.todo_tasks, function(todo_task, index) {
          todo_listFactory.updateTodoTask({todo_list_id: todo_task.todo_list_id, id: todo_task.id, priority: index}) 
        })
      },
      helper: function (e, ui) {
        ui.children().each(function() {
          $(this).width($(this).width());
        });
        return ui;
      },
      items: 'tr:not(.not-sortable)',
      axis: 'y'
    };
}]);
